import React from "react";
import '../styles/LoginModal.css';
import { ReactComponent as MainLogo } from '../img/MainLogo.svg';
const Modal = ({ isOpen, onClose }) => {
    return (
        <>
            {isOpen && (
                <div className="modal">
                    <div className="modalContent">
                        <MainLogo width="102" height="105.3" style={{marginLeft:'29%',marginTop:'10%'}}/>
                        <h2 style={{textAlign:'center',fontSize:'44px',fontWeight:'600'}}>TTOON 시작하기</h2>
                        <p style={{color:'#949494',textAlign:'center'}}>평범한 하루를 특별한 만화로 기록해보아요</p>
                        {/* 모달 닫기 버튼 */}
                        <button onClick={onClose}>닫기</button>
                    </div>
                </div>
            )}
        </>
    );
};

export default Modal;
