import React, { useState } from "react";
import { ReactComponent as MainLogo } from '../img/MainLogo.svg';
import '../styles/Header.css';
import LoginModal from "./LoginModal";
import SettingsModal from "./SettingsModal"; // SettingsModal import 추가

const Header = () => {
    const [modalOpen, setModalOpen] = useState(false);
    const [settingsModalOpen, setSettingsModalOpen] = useState(false); // 설정 모달 상태 추가

    const openModal = () => {
        setModalOpen(true);
    };

    const closeModal = () => {
        setModalOpen(false);
    };

    const openSettingsModal = () => {
        setSettingsModalOpen(true);
    };

    const closeSettingsModal = () => {
        setSettingsModalOpen(false);
    };

    return (
        <div className="HeaderWrapper">
            <MainLogo width="49" height="50.7"/>
            <div className="HeaderMenu">
                <div className="HeaderItem">
                    <button onClick={openSettingsModal}>버튼 3</button> {/* 버튼 3 클릭 시 설정 모달 열기 */}
                </div>
                <div className="HeaderItem">버튼</div>
                <button onClick={openModal} className="HeaderLoginButton">시작하기</button>
                <LoginModal isOpen={modalOpen} onClose={closeModal} />
                <SettingsModal isOpen={settingsModalOpen} onClose={closeSettingsModal} /> {/* 설정 모달 추가 */}
            </div>
        </div>
    );
}

export default Header;
