import React, { useState } from "react";
import "../styles/SettingsModal.css";
import { AiOutlineClose } from "react-icons/ai";

const SettingsModal =({isOpen, onClose}) =>{
    const [selectedTab, setSelectedTab] = useState('account'); // 기본 탭은 계정 설정

    const handleTabChange = (tab) => {
        setSelectedTab(tab);
    };

    // 선택된 탭에 따라 해당하는 컨텐츠를 반환
    const renderTabContent = () => {
        switch(selectedTab) {
            case 'account':
                return (
                    <div className="AccountSettings">
                        계정 설정 컨텐츠 
                    </div>
                );
            case 'notification':
                return (
                    <div className="NotificationSettings">
                        알림 설정 컨텐츠 
                    </div>
                );
            case 'language':
                return (
                    <div className="LanguageSettings">
                    언어 설정 컨텐츠 
                    </div>
                );
            default:
                return null;
        }
    };

    return(
        <>
            {isOpen &&(
                <div className="ModalWrapper">
                    <div className="Modal">
                        <AiOutlineClose onClick={onClose} style ={{color:'#9c9c9c',width:'28px', height:'28px',marginLeft:'58vw',marginTop:'1vh'}}/>
                        <div className="SideBar">
                            <div className="Profile">
                                {/* 프로필 정보 */}
                            </div>
                            <div className="SideBarSetting">
                                {/* 탭들 */}
                                <li onClick={() => handleTabChange('account')}>계정 설정</li>
                                <li onClick={() => handleTabChange('notification')}>알림 설정</li>
                                <li onClick={() => handleTabChange('language')}>언어 설정</li>
                            </div>
                            <div className="Information">
                                {/* 정보 및 문의 */}
                            </div>
                        </div>
                        <div className="SettingsContents">
                            {/* 선택된 탭에 따른 컨텐츠 */}
                            {renderTabContent()}
                        </div>
                    </div>
                </div>
            )}
        </>
    );
}

export default SettingsModal;
