import React, { useState } from 'react';

const SettingsModal = () => {
    const [selectedSection, setSelectedSection] = useState('account'); // 기본적으로 계정 설정을 선택하도록 설정

    const handleSectionChange = (section) => {
        setSelectedSection(section);
    };

    return (
        <div className="modalContent">
            <div className="modalHeader">
                <h2>설정</h2>
            </div>
            <div className="modalBody">
                <div className="sidebar">
                    <ul>
                        <li className={selectedSection === 'account' ? 'active' : ''} onClick={() => handleSectionChange('account')}>계정 설정</li>
                        <li className={selectedSection === 'notification' ? 'active' : ''} onClick={() => handleSectionChange('notification')}>알림 설정</li>
                        {/* 다른 섹션 추가 */}
                    </ul>
                </div>
                <div className="content">
                    {selectedSection === 'account' && (
                        <div>
                            {/* 계정 설정 내용 */}
                            <h3>계정 설정</h3>
                            <p>여기에 계정 설정에 관한 내용이 나옵니다.</p>
                        </div>
                    )}
                    {selectedSection === 'notification' && (
                        <div>
                            {/* 알림 설정 내용 */}
                            <h3>알림 설정</h3>
                            <p>여기에 알림 설정에 관한 내용이 나옵니다.</p>
                        </div>
                    )}
                    {/* 다른 섹션에 대한 내용 추가 */}
                </div>
            </div>
        </div>
    );
};

export default SettingsModal;
