import React, { useState } from "react";
import "../styles/SettingsModal.css";
import { AiOutlineClose } from "react-icons/ai";

const SettingsModal = ({ isOpen, onClose }) => {
    const [selectedTab, setSelectedTab] = useState('account'); // 기본 탭은 계정 설정

    const handleTabChange = (tab) => {
        setSelectedTab(tab);
    };

    const renderTabContent = () => {
        switch (selectedTab) {
            case 'account':
                return (
                    <div className="AccountSettings">
                        계정 설정 컨텐츠 
                    </div>
                );
            case 'notification':
                return (
                    <div className="NotificationSettings">
                        알림 설정 컨텐츠 
                    </div>
                );
            // 나머지 탭의 컨텐츠는 동일하게 추가
            default:
                return null;
        }
    };

    return (
        <>
            {isOpen && (
                <div className="ModalWrapper">
                    <div className="Modal">
                        <div className="ModalHeader">
                            <AiOutlineClose onClick={onClose} className="CloseIcon" />
                        </div>
                        <div className="ModalBody">
                            <div className="SideBar">
                                <div className="Profile">
                                    <div className="ProfileInfo">
                                        <div className="ProfileImage"></div>
                                        <div className="ProfileName">지윤석 님</div>
                                        <div className="ProfilePoints">200P</div>
                                    </div>
                                </div>
                                <div className="SideBarSetting">
                                    설정
                                    <div className="SideBarSettingItem">
                                        <div className={selectedTab === 'account' ? 'active' : ''} onClick={() => handleTabChange('account')}>계정 설정</div>
                                        <div className={selectedTab === 'notification' ? 'active' : ''} onClick={() => handleTabChange('notification')}>알림 설정</div>
                                        {/* 나머지 탭의 요소도 동일하게 추가 */}
                                    </div>
                                </div>
                            </div>
                            <div className="SettingsContents">
                                {renderTabContent()}
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
};

export default SettingsModal;
