import React, { useEffect, useCallback } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { ReactComponent as MainLogo } from '../img/MainLogo.svg';
import '../styles/LoginModal.css';
import { AiOutlineClose } from "react-icons/ai";
import GoogleLogin from "./GoogleLogin";
import KakaoLogin from "./KakaoLogin";
import AppleLogin from "./AppleLogin";
import axios from 'axios';

const LoginModal = ({ isOpen, onClose }) => {
  const location = useLocation();
  const navigate = useNavigate();

  const loginWithProvider = useCallback(async (provider, code) => {
    try {
      const response = await axios.post('/api/auth/app/login', {
        providerId: code,
        provider: provider,
      });

      if (response.data.isSuccess) {
        const { accessToken } = response.data.data;
        localStorage.setItem('accessToken', accessToken);
        navigate('/'); // 로그인 성공 후 메인 페이지로 이동합니다.
      } else {
        console.error('로그인 실패:', response.data.message);
      }
    } catch (error) {
      console.error('로그인 중 오류 발생:', error);
    }
  }, [navigate]);

  useEffect(() => {
    const urlParams = new URLSearchParams(location.search);
    const code = urlParams.get('code');
    const state = urlParams.get('state');

    if (code && state) {
      let provider = '';
      if (state.includes('kakao')) {
        provider = 'kakao';
      } else if (state.includes('google')) {
        provider = 'google';
      } else if (state.includes('apple')) {
        provider = 'apple';
      }
      loginWithProvider(provider, code);
    }
  }, [location, loginWithProvider]);

  return (
    <>
      {isOpen && (
        <div className="modalWrapper">
          <div className="modal">
            <div className="modalContent">
              <AiOutlineClose onClick={onClose} style={{ color: '#9c9c9c', width: '36px', height: '36px', marginLeft: '30vw', marginTop: '1vh' }} />
              <MainLogo width="102" height="105.3" style={{ marginLeft: '40%', marginTop: '10%' }} />
              <div style={{ color: 'black', textAlign: 'center', fontSize: '44px', fontWeight: '600', marginTop: '32px' }}>TTOON 시작하기</div>
              <div style={{ color: '#949494', textAlign: 'center', fontSize: '20px', fontWeight: '500' }}>평범한 하루를 특별한 만화로 기록해보아요</div>
              <div className="SocialLogin">
                <AppleLogin />
                <KakaoLogin />
                <GoogleLogin />
              </div>
              <div className="Policy">
                이용약관   &nbsp; |  &nbsp; 개인정보처리방침
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default LoginModal;
